// Parse LLM analysis results and unit test generation
const llmData = $node["LLM Analysis via MCP"].json.analysis;
const speculatorData = $node["Speculator Bot Analysis"].json.analysis;
const buildData = $node["Extract Build Data"].json;
const unitTestData = items[0].json;

// Generate unique branch name with timestamp to avoid conflicts
const shortSHA = buildData.commitSHA.substring(0, 7);
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').substring(0, 19);
const newBranchName = `ai-improvements-${shortSHA}-${timestamp}`;

// Prepare PR body with unit test information
let testSummary = '';
if (unitTestData.success && unitTestData.generatedTests) {
  testSummary = `\n### ðŸ§ª Generated Unit Tests\n${unitTestData.generatedTests.map(test => 
    `- **${test.filename}**: ${test.description || ('Tests for ' + test.source_file)} (${test.testCount || test.test_methods_count || 1} tests)`
  ).join('\n')}\n\n**Test Generation Summary**: ${unitTestData.generatedTests.length} test files created\n`;
} else if (unitTestData.generated_tests) {
  testSummary = `\n### ðŸ§ª Generated Unit Tests\n${unitTestData.generated_tests.map(test => 
    `- **${test.filename}**: Tests for ${test.source_file} (${test.test_methods_count} tests)`
  ).join('\n')}\n\n**Test Generation Summary**: ${unitTestData.generated_tests.length} test files created\n`;
}

const prBody = `## ðŸ¤– AI-Generated Code Analysis & Improvements

### Summary
**Commit**: ${buildData.commitSHA}
**Branch**: ${buildData.branch}
**Author**: ${buildData.author}
**Build**: #${buildData.buildNumber}

---

### ðŸ“Š Risk Assessment
- **Risk Score**: ${speculatorData.deploymentRiskScore.toFixed(2)} (${speculatorData.riskLevel})
- **Files Changed**: ${speculatorData.changeSummary.filesChanged}
- **Lines Added**: ${speculatorData.changeSummary.linesAdded}
- **Lines Removed**: ${speculatorData.changeSummary.linesRemoved}

---

### ðŸ” Change Analysis
${llmData.changeIntroduced}

---

### âš ï¸ Gap Analysis
${llmData.gapAnalysis.map(gap => 
  `**${gap.category}**: ${gap.concern}\n*Recommendation*: ${gap.recommendation}`
).join('\n\n')}

---
${testSummary}
### ðŸ§ª Test Coverage Recommendations
${llmData.testCoverage.map(test => 
  `- **${test.priority}**: ${test.suggestion} (${test.reasoning})`
).join('\n')}

---

### ðŸ”’ Security Fixes
${llmData.securityFixes.map(fix => 
  `- **${fix.type}** [${fix.priority}]: ${fix.action}`
).join('\n')}

---

### ðŸ“ˆ Metrics
- **Test Coverage**: ${(speculatorData.testSelection.coverageScore * 100).toFixed(1)}%
- **Recommended Tests**: ${speculatorData.testSelection.totalTests}
- **Est. Test Time**: ${speculatorData.testSelection.estimatedTimeMinutes.toFixed(1)} minutes

---

### ðŸ”— Links
- [Jenkins Build](${buildData.jenkinsURL})
- [SonarQube Report](${buildData.sonarURL})

---

*Generated by AI-Enhanced DevSecOps Pipeline*
`;

return [
  {
    json: {
      newBranchName,
      prTitle: `AI Analysis: ${llmData.changeIntroduced.substring(0, 80)}...`,
      prBody,
      baseBranch: buildData.branch,
      commitSHA: buildData.commitSHA,
      hasImprovements: llmData.testCoverage.length > 0 || llmData.securityFixes.length > 0,
      analysis: llmData,
      risk: speculatorData,
      unitTests: unitTestData
    }
  }
];

// Handle GitHub branch creation with conflict resolution
const buildData = $('Extract Build Data').first().json;
const parseData = $input.first().json;
const unitTestData = $('Generate Unit Test Code').first().json;
const shortSHA = buildData.commitSHA.substring(0, 7);
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').substring(0, 19);
const millis = Date.now() % 10000;

// Try multiple branch name strategies
const branchCandidates = [
  parseData.newBranchName, // Original timestamped name
  `ai-improvements-${shortSHA}-${millis}`, // With milliseconds
  `ai-fixes-${shortSHA}-${timestamp}`, // Alternative prefix
  `automated-improvements-${shortSHA}-${Date.now()}` // Fallback with full timestamp
];

// Return the first candidate (the timestamped version should work)

// Comprehensive logging
console.log('[DEBUG] Preparing to create branch with the following data:');
console.log(`  - Branch Name: ${branchCandidates[0]}`);
console.log(`  - Repository: ${buildData.repository}`);
console.log(`  - Commit SHA: ${buildData.commitSHA}`);

return [{
  json: {
    branchName: branchCandidates[0],
    fallbackBranches: branchCandidates.slice(1),
    repository: buildData.repository,
    commitSHA: buildData.commitSHA,
    originalData: parseData,
    unitTests: unitTestData
  }
}];

// Log the exact data being sent to GitHub API for branch creation
const inputData = $input.first().json;

console.log('[DEBUG] GitHub Branch Creation Request Data:');
console.log(`  - Repository: ${inputData.repository}`);
console.log(`  - Branch Name: ${inputData.branchName}`);
console.log(`  - Commit SHA: ${inputData.commitSHA}`);
console.log(`  - Repository (cleaned): ${inputData.repository.replace('https://github.com/', '').replace('.git', '')}`);
console.log(`  - Full ref: refs/heads/${inputData.branchName}`);

// Validate the data
if (!inputData.repository || !inputData.branchName || !inputData.commitSHA) {
  console.error('[ERROR] Missing required data for branch creation:');
  console.error(`  - Repository: ${inputData.repository}`);
  console.error(`  - Branch Name: ${inputData.branchName}`);
  console.error(`  - Commit SHA: ${inputData.commitSHA}`);
}

return $input.all();

// Process unit test files for GitHub commits
const branchData = $input.first().json;
const unitTestData = $('Generate Unit Test Code').first().json;
const buildData = $('Extract Build Data').first().json;
const branchName = branchData.branchName;

// Debug input data
console.log(`[INPUT DEBUG] Branch data received:`, JSON.stringify(branchData));
console.log(`[INPUT DEBUG] Branch name from input: ${branchName}`);
console.log(`[INPUT DEBUG] Build data branch: ${buildData.branch}`);

// Check if branch creation was successful
const branchResponse = $('Create GitHub Branch').first();
let actualBranchName = branchName;

// The branch creation node now ignores HTTP status errors, so we check the output
const branchBody = branchResponse.json.body || {};
if (branchResponse.json.statusCode && branchResponse.json.statusCode !== 201) {
  // 422 means it already exists, which is fine.
  if (branchResponse.json.statusCode === 422 && branchBody.message && branchBody.message.includes('Reference already exists')) {
      console.log('Branch already exists, proceeding.');
  } else {
      console.log(`Branch creation may have failed with status ${branchResponse.json.statusCode}, using fallback branch name.`);
  }
} else {
    console.log('Branch created successfully or status ignored.');
}


let commits = [];

// Handle both generatedTests and generated_tests field names
const generatedTests = unitTestData?.generatedTests || unitTestData?.generated_tests || [];

if (unitTestData?.success && Array.isArray(generatedTests) && generatedTests.length > 0) {
  // Filter out tests with empty or missing content and ensure unique filenames
  const filteredTests = generatedTests.filter(test => test && typeof test === 'object' && test.content && typeof test.content === 'string' && test.content.trim() !== '' && test.filename);
  const uniqueTests = new Map();

  filteredTests.forEach((test, index) => {
    // Generate proper Java class name and filename
    let originalFilename = test.filename || 'UnknownFile';

    console.log(`[DEBUG] Processing filename: ${originalFilename}`);

    // Extract base name from filename (remove extension first)
    let baseName = originalFilename.replace(/\.[^.]*$/, ''); // Remove file extension
    baseName = baseName.replace(/[^a-zA-Z0-9]/g, ''); // Remove special characters

    // Handle empty base names
    if (!baseName || baseName.length === 0) {
      baseName = 'GeneratedTest';
    }

    // Create proper Java class name: capitalize first letter, lowercase rest
    let baseClassName = baseName.charAt(0).toUpperCase() + baseName.slice(1).toLowerCase();

    // Add Test suffix only if not already present
    if (!baseClassName.toLowerCase().endsWith('test')) {
      baseClassName += 'Test';
    }

    // Create final filename
    let filename = baseClassName + '.java';

    // Handle duplicates by appending index
    let finalClassName = baseClassName;
    if (uniqueTests.has(filename)) {
      finalClassName = baseClassName.replace('Test', '') + (index + 1) + 'Test';
      filename = finalClassName + '.java';
    }

    console.log(`[DEBUG] Class name transformation: ${originalFilename} -> ${finalClassName}`);

    // Fix the Java content to use proper class names and syntax
    let fixedContent = test.content;

    // Replace invalid class declarations
    fixedContent = fixedContent.replace(/public class [^{\s]+\s*{/g, `public class ${finalClassName} {`);

    // Replace invalid variable declarations with proper mock objects
    fixedContent = fixedContent.replace(/@Mock\s*private [^;]+;/g, '@Mock\n    private Object mockObject;');

    // Fix DisplayName annotation
    fixedContent = fixedContent.replace(/@DisplayName\([^)]*\)/g, `@DisplayName("${finalClassName} - Unit Tests")`);

    // Ensure package statement is valid
    if (!fixedContent.includes('package ')) {
      fixedContent = 'package com.example.test;\n\n' + fixedContent;
    }

    console.log(`[DEBUG] Fixed Java class: ${originalFilename} -> ${filename} (class: ${finalClassName})`);

    uniqueTests.set(filename, {
      path: `tests/${filename}`,
      content: fixedContent,
      message: `Add unit tests for ${test.source_file || 'code changes'}`
    });
  });

  commits = Array.from(uniqueTests.values());

  // If no valid tests after filtering, add placeholder
  if (commits.length === 0) {
    commits = [{
      path: 'tests/README.md',
      content: `# AI-Generated Tests\n\nUnit test generation completed but no valid test content was produced.\n\nGenerated on: ${new Date().toISOString()}\nCommit: ${buildData.commitSHA}\nOriginal tests attempted: ${generatedTests.length}\n\nIssues found:\n- Empty or missing test content\n- Invalid test file structure\n`,
      message: 'Add AI-generated tests directory (no valid tests generated)'
    }];
  }
} else {
  // Create a placeholder commit to ensure the workflow continues
  commits = [{
    path: 'tests/README.md',
    content: `# AI-Generated Tests\n\nThis directory contains AI-generated unit tests.\n\nGenerated on: ${new Date().toISOString()}\nCommit: ${buildData.commitSHA}\nUnit test generation status: ${unitTestData?.success ? 'successful but no tests' : 'failed'}\n\nDebug info:\n- Success: ${unitTestData?.success}\n- Generated tests count: ${Array.isArray(generatedTests) ? generatedTests.length : 0}\n`,
    message: 'Add AI-generated tests directory'
  }];
}

console.log(`Prepared ${commits.length} commits for test files on branch ${actualBranchName}`);
console.log(`[DEBUG] Branch information: ${actualBranchName}`);
console.log(`[DEBUG] Repository information: ${buildData.repository}`);

// Add common metadata to all commits and perform final validation
const commitsWithMetadata = commits.map((commit, index) => {
    const finalCommit = {
        path: commit.path || 'tests/error.md',
        content: commit.content || '# Error: Content was missing',
        message: commit.message || 'Fix: Commit message was missing',
        branch: actualBranchName
    };

    // Validate required fields before logging
    if (!finalCommit.path || !finalCommit.content || !finalCommit.message || !finalCommit.branch) {
        console.error(`[ERROR] Item ${index} missing required fields:`);
        console.error(`  - Path: ${!!finalCommit.path}`);
        console.error(`  - Content: ${!!finalCommit.content}`);
        console.error(`  - Message: ${!!finalCommit.message}`);
        console.error(`  - Branch: ${!!finalCommit.branch}`);
    }

    // Comprehensive logging for each item
    console.log(`[DEBUG] Item ${index} prepared for commit:`);
    console.log(`  - Path: ${finalCommit.path}`);
    console.log(`  - Message: ${finalCommit.message}`);
    console.log(`  - Branch: ${finalCommit.branch}`);
    console.log(`  - Content Length: ${finalCommit.content.length}`);
    console.log(`  - Has Content: ${!!finalCommit.content}`);
    // Log a snippet of the content to verify it's not empty
    console.log(`  - Content Snippet: ${(finalCommit.content || '').substring(0, 100).replace(/\n/g, ' ')}...`);

    // Final verification that branch is set
    console.log(`[FINAL CHECK] Item ${index} - Branch before return: ${finalCommit.branch}`);
    console.log(`[FINAL CHECK] Item ${index} - Complete object:`, JSON.stringify(finalCommit, null, 2));

    return { json: finalCommit };
});

return commitsWithMetadata;

// Prepare GitHub API request based on file existence
const inputData = $('Prepare Test File Commits').first().json;
const checkResponse = $input.first().json;
const buildData = $('Extract Build Data').first().json;

// Extract repository name from URL
const repoName = buildData.repository.replace('https://github.com/', '').replace('.git', '');

// Check if file exists (status 200) or not (status 404)
const fileExists = checkResponse.statusCode === 200;
let sha = null;

if (fileExists && checkResponse.sha) {
  sha = checkResponse.sha;
  console.log(`[DEBUG] File exists, using SHA: ${sha}`);
} else {
  console.log(`[DEBUG] File does not exist, creating new file`);
}

// Prepare the request body - only include SHA if file exists
const requestBody = {
  message: inputData.message,
  content: Buffer.from(inputData.content).toString('base64'),
  branch: inputData.branch
};

// Add SHA only if file exists
if (sha) {
  requestBody.sha = sha;
}

const githubRequest = {
  url: `https://api.github.com/repos/${repoName}/contents/${inputData.path}`,
  body: JSON.stringify(requestBody),
  message: inputData.message,
  content: Buffer.from(inputData.content).toString('base64'),
  branch: inputData.branch
};

// Add SHA to top level only if it exists (for bodyParameters fallback)
if (sha) {
  githubRequest.sha = sha;
}

console.log(`[DEBUG] Prepared GitHub request for: ${inputData.path}`);
console.log(`[DEBUG] URL: ${githubRequest.url}`);
console.log(`[DEBUG] Message: ${githubRequest.message}`);
console.log(`[DEBUG] Branch: ${githubRequest.branch}`);
console.log(`[DEBUG] Content length: ${githubRequest.content.length}`);
console.log(`[DEBUG] File exists: ${fileExists}`);
console.log(`[DEBUG] Using SHA: ${sha || 'none (new file)'}`);
console.log(`[DEBUG] Request body: ${githubRequest.body}`);

return [{ json: githubRequest }];
